import React, { useState } from "react";


const MyAccordian = ({ question, answer }) => {
    const [show, setShow] = useState(false);

    return (
        <>
            <div className="main-heading">
                <p className="detail"
                    onClick={() => {
                        setShow(!show);
                    }}>{!show ? "➕" : "➖"}</p>
                <h3>{question}</h3>
            </div>
            {
                show && <p> {answer} </p>
            }

        </>
    );
}

export default MyAccordian;
